// 罗盘置信度（新增）
export function calculateConfidence(p:{calibrationAge:number, stability:number, noise:number, motion:number}):number {
  const w = {a:0.3, s:0.3, n:0.2, m:0.2};
  // 归一化略，输出[0,1]
  return Math.max(0, Math.min(1, w.a*p.calibrationAge + w.s*p.stability + w.n*(1-p.noise) + w.m*(1-p.motion)));
}

